/**
 * get_userdata3 method
 *
 * @return void
 */
    public function get_user_data3($username){
        $client_id = Configure::read('Instagram.tabiatejahan.client_id');
        $html = file_get_contents('https://api.instagram.com/v1/users/search?q=' . $username . '&client_id=' . $client_id);
        $decode = json_decode($html);
        if (!empty($decode->data)) {
            $user = $decode->data[0];
            $user = $this->user($user);
            return $user;            
        }else{
            return false;
        }        
    }



    public function get_user_data4($user_id){
            // $client_id = Configure::read('Instagram.tabiatejahan.client_id');
            $client_id = Configure::read('Instagram.ninijan.client_id');
            $html = file_get_contents('https://api.instagram.com/v1/users/' . $user_id . '/?client_id=' . $client_id);
            $decode = json_decode($html);
            if (!empty($decode->data)) {
                return $decode;            
            }else{
                return false;
            }        
        }



/**
 * get_user_data2 method
 *
 * @return void
 */
    public function get_user_data2($username){
        $html = file_get_contents('http://instagram.com/'.$username);
        debug($html);
        die();
        // Create a new DOMDocument to parse the result in
        $doc = new DOMDocument();
        //supress the HTML5 tags warning //
        libxml_use_internal_errors(true);
        // Load content from Instagram //
        $doc->loadHTML($html);
        // set errors back to normal state //
        libxml_use_internal_errors(false);
        // Create a new Xpath from domDoc //
        $xpath = new DOMXPath($doc);
        debug($xpath);
        die();
        //query all the script tags // 
        $tags = $xpath->query('//script');
        debug($tags);
        die();
        foreach ($tags as $tag) {            
            // lets find a match for window._jscalls //
            if(strlen($tag->nodeValue) > 10000){
        
                $arr = explode("\n", $tag->nodeValue);

                // check each rows for userprofile string
                foreach($arr as $k => $value) 
                {
                        if(preg_match('/UserProfile/i',$value)) 
                        {
                                $useIndex = $k;
                        }
                }

                // set the index //
                $useIndex?$useIndex:'';

                $json_string = $arr[$useIndex];
                $json_string = trim($json_string);
                
                $json_string = str_replace("window._sharedData = ",'',$json_string);
                $json_string = substr_replace($json_string ,"",-1,1);

                // decode the string to an object //
                $phinstagram_json_object = json_decode($json_string);
            }
        }

        return $phinstagram_json_object;
        
    }









